require_once ABSPATH . 'wp-admin/includes/image.php';
require_once ABSPATH . 'wp-admin/includes/file.php';
require_once ABSPATH . 'wp-admin/includes/media.php';

$images = [
    'https://picsum.photos/seed/abcs1/1600/900',
    'https://picsum.photos/seed/abcs2/1600/900',
    'https://picsum.photos/seed/abcs3/1600/900',
    'https://picsum.photos/seed/abcs4/1600/900',
    'https://picsum.photos/seed/abcs5/1600/900',
    'https://picsum.photos/seed/abcs6/1600/900'
];

foreach ( $images as $index => $image_url ) {
    $number  = str_pad( strval( $index + 1 ), 2, '0', STR_PAD_LEFT );
    $post_id = wp_insert_post( [
        'post_title'   => 'Carousel Demo Post ' . $number,
        'post_content' => 'Activez le mode carousel sur ce bloc Query Loop pour tester le plugin.',
        'post_status'  => 'publish',
        'post_type'    => 'post'
    ] );

    if ( is_wp_error( $post_id ) ) {
        continue;
    }

    $attachment_id = media_sideload_image( $image_url, $post_id, 'Image de démonstration #' . $number, 'id' );
    if ( ! is_wp_error( $attachment_id ) ) {
        set_post_thumbnail( $post_id, $attachment_id );
    }
}
